import React from 'react';
import Link from 'next/link';
import Head from 'next/head';
import axios from "axios";
// import { Container } from './styles';
const Home = React.FC = (props) => {
  return (
    <div>
      <Head>
        <title>Heverson Damasceno - Fullstack</title>
      </Head>
      <section>
        <h2> my repos </h2>
        <div>
          {props.repositories && props.repositories.map(repo =>(
            <div>
              <h3>{repo.name}</h3>
              <Link href={repo.owner.url}><a>Visualizar</a></Link>
            </div>
          ))}
        </div>
      </section>  
    </div>
  )
}
Home.getInitialProps = async () => {
  const response  = await axios.get(
    'https://api.github.com/users/heverson/repos'
  );
  return {repositories: response.data}
}
export default Home;
