import styled from 'styled-components';

export const Container = styled.div`
  flex:1;
`;
export const Header = styled.header`
  flex:1;
  justify-content: space-between;
  flex-direction: row;
  img{
    max-width: 200px;
  }
  h1{
    font-weight: 'bold';
    font-size: 50px;
  }

`;

export const Cards = styled.div`
  flex:1;
  justify-content: space-between;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  & > div{
    color: #1AE8AA;
    background: #fff;
    max-width:300px;
    padding:10px;
    margin:10px;
  }
`;
