import React from 'react';
import Link from 'next/link';
import Head from 'next/head';
import axios from "axios";
// import { Container } from './styles';
const Home = React.FC = ({repositories}) => {
  return (
    <div>
      <Head>
        <title>Heverson Damasceno - Fullstack</title>
      </Head>
      <section>
        <h2>github my repos </h2>
        <div>
          {repositories && repositories.map(repo =>(
            <div>
              <h3>{repo.name}</h3>
              <p>
                {repo.description}
              </p>
              <Link href={repo.owner.url}><a>Visualizar</a></Link>
            </div>
          ))}
        </div>
      </section>  
    </div>
  )
}
Home.getInitialProps = async (ctx) => {
  const response  = await axios.get(
    'https://api.github.com/users/heverson/repos'
  );
  console.log(reponse.data);
  return {repositories: response.data}
}
export default Home;
