import styled from 'styled-components';

export const Container = styled.div`
section{
  & > h2{
    color: #1AE8AA;
  }
}  
`;
export const Header = styled.header`
  flex:1;
  justify-content: space-between;
  flex-direction: row;
  img{
    max-width: 200px;
  }
  h1{
    font-weight: 'bold';
    font-size: 50px;
  }
`;

export const Cards = styled.div`
  flex-direction: row;
  align-items: stretch;
  justify-content: start;
  & > div{
    color: #303030;
    background: #fff;
    max-width:310px;
    width:100%;
    padding:10px;
    margin:10px;
  }
`;
